    private static int jetlen = jets.length(); 
    private static int jet_ctr = 0;
    private static Map <String, int[]> movemap = new HashMap<String, int[]>();


    private static void init() {
        movemap.put("<", new int[] {-1, 0});
        movemap.put(">", new int[] {1, 0});
        movemap.put("down", new int[] {0, -1});
    }

    public static void main(String[] args) {
        System.out.println(jetlen);
        init();
        Grid grid = new Grid();
        // BigInteger total = BigInteger.ZERO;
        // BigInteger limit = new BigInteger("100000000");
        // for (BigInteger n=BigInteger.ZERO; !n.equals(limit); n=n.add(BigInteger.ONE)) {
        for (long n=0; n < 100000; n++) {
            int[][] piece = grid.init_piece(PIECES[piece_ctr]);

            while (piece != null) {
                // if (jet_ctr == 0 && grid.topfill()) {
                //     System.out.println("YEAH! " + piece_ctr);
                // }
                int[] dir = movemap.get(jets.substring(jet_ctr, jet_ctr+1));
                int[][] hor_move = grid.move_piece(piece, dir);
                jet_ctr = (jet_ctr + 1) % jetlen;

                dir = movemap.get("down");
                int[][] down_move = grid.move_piece(hor_move, dir);

                piece = down_move;
                if (hor_move == down_move) {
                    grid.insert_piece(piece);
                    piece = null;
                }
            }
            if (n == 1870) {
                System.out.println(piece_ctr + " ::: " + jet_ctr + " ::: " + n + " ::: " + grid.get_cum_top());
            }
            if ((grid.get_top() - 349) % 2644 == 0) {
                System.out.println(n + " ::: " + grid.get_top() + " ::: " + grid.get_cum_top());
            }
            // if (n == 120) {
            //     System.out.println(piece_ctr + " ::: " + jet_ctr + " ::: " + n + " ::: " + grid.get_cum_top());
            // }
            // if ((grid.get_top() - 25) % 106 == 0) {
            //     System.out.println(piece_ctr + " ::: " + jet_ctr + " ::: " + n + " ::: " + grid.get_cum_top());
            // }
            piece_ctr = (piece_ctr + 1) % 5;
            // if (n % 10000 == 0) {
            //     // System.out.println(grid.get_cum_top());
            //     BigInteger diff = grid.get_cum_top().subtract(total);
            //     total = total.add(diff);
            // }
        }
        System.out.println(grid.get_cum_top());
        grid.check_pattern();
        // grid.print_field(0, 140);
    }
